import React from 'react';
import { shallow } from 'enzyme';
import AppSideNav from 'components/Display/AppBar/components/AppSideNav';

describe('AppSideNav', () => {
  let props;
  let component;

  beforeAll(() => {
    props = {
      id: 'id-1234',
      className: 'class-1234',
      containerClassName: 'container-class-1234',
      width: '300px',
      navContent: <div>Hello World</div>,
      children: <div>Main Content</div>,
    };

    component = shallow(<AppSideNav {...props} />);
  });

  it('renders', () => {
    expect(component.find('.ui-app-bar-side-nav')).toHaveLength(1);
  });

  it('renders both the side and main content', () => {
    expect(component.find('.ui-app-bar-side-nav__side-content')).toHaveLength(1);
    expect(component.find('.ui-app-bar-side-nav__main-content')).toHaveLength(1);
  });
});
