import React from 'react';
import { shallow } from 'enzyme';
import Avatar from 'components/Display/Avatar';

describe('Avatar', () => {
  it('renders', () => {
    const result = shallow(<Avatar />);
    expect(result.find('figure')).toHaveLength(1);
  });

  it('can be small', () => {
    const result = shallow(<Avatar size="sm" />);
    expect(result.find('.avatar--small')).toHaveLength(1);
  });

  it('can be large', () => {
    const result = shallow(<Avatar size="lg" />);
    expect(result.find('.avatar--large')).toHaveLength(1);
  });

  it('doesn\'t render if not shouldRender', () => {
    const result = shallow(<Avatar shouldRender={false} />);
    expect(result.find('figure')).toHaveLength(0);
  });

  it('renders the fallbackIcon', () => {
    const props = {
      fallbackIcon: 'IconElectronsArrow',
      image: '',
    };
    const result = shallow(<Avatar {...props} />);
    expect(result.find('Icon')).toHaveLength(1);
  });
});
