import React from 'react';
import { shallow } from 'enzyme';
import BaseCardBody from 'components/Display/BaseCard/components/BaseCardBody';

describe('BaseCardBody', () => {
  it('renders', () => {
    const wrapper = shallow(
      <BaseCardBody>
        <p>Pete!!</p>
      </BaseCardBody>,
    );

    expect(wrapper.find('.ui-base-card-body')).toHaveLength(1);
  });

  it('renders with an optional id', () => {
    const wrapper = shallow(
      <BaseCardBody id="id-1234">
        <p>Pete!!</p>
      </BaseCardBody>,
    );

    expect(wrapper.find('#id-1234')).toHaveLength(1);
  });
});
