import React from 'react';
import { shallow } from 'enzyme';
import BaseCardHeader from 'components/Display/BaseCard/components/BaseCardHeader';

describe('BaseCardHeader', () => {
  const props = {
    title: 'Hey Pete!',
  };

  it('renders a h2 tag', () => {
    const wrapper = shallow(
      <BaseCardHeader
        {...props}
      >
        <div>Pete, Pete, Pete!</div>
      </BaseCardHeader>,
    );

    expect(wrapper.find('h2')).toHaveLength(1);
    expect(wrapper.find('h2').text()).toEqual('Hey Pete!');
  });


  it('renders passed in children in a ui-base-card-header__children-item class', () => {
    const wrapper = shallow(
      <BaseCardHeader
        {...props}
        id="2"
      >
        <div>Pete, Pete, Pete!</div>
        <div>Pete!!!</div>
      </BaseCardHeader>,
    );

    expect(wrapper.find('.ui-base-card-header__children')).toHaveLength(1);
    expect(wrapper.find('.ui-base-card-header__children-item')).toHaveLength(2);
  });
});
