import React from 'react';
import { shallow } from 'enzyme';
import BaseCard from 'components/Display/BaseCard';

describe('BaseCard', () => {
  it('renders', () => {
    const wrapper = shallow(<BaseCard><div>Pete!</div></BaseCard>);

    expect(wrapper.find('.ui-base-card--bordered')).toHaveLength(1);
  });

  it('renders a borderless BaseCard', () => {
    const wrapper = shallow(<BaseCard id="1" noBorder><div>Pete!</div></BaseCard>);

    expect(wrapper.find('.ui-base-card')).toHaveLength(1);
  });
});
