import React from 'react';
import { shallow } from 'enzyme';
import CardActions from 'components/Display/Card/components/CardActions';

describe('CardActions', () => {
  let props;
  let component;

  beforeAll(() => {
    props = {
      id: 'id-1234',
      className: 'className-1234',
      children: <div>Hello World</div>,
    };

    component = shallow(<CardActions {...props} />);
  });

  it('renders', () => {
    expect(component.find('.ui-card-actions')).toHaveLength(1);
    expect(component.find('.ui-card-actions').text()).toBe('Hello World');
  });
});
