import React from 'react';
import { shallow } from 'enzyme';
import Drawer from 'components/Display/Drawer';

describe('Drawer', () => {
  let props;
  let component;

  describe('opened drawer with primary location', () => {
    beforeAll(() => {
      props = {
        id: 'id-1234',
        className: 'className-1234',
        open: true,
        width: '250px',
        onEscClick: jest.fn(),
        children: <div>Hello World</div>,
      };

      component = shallow(<Drawer {...props} />);
    });

    it('renders', () => {
      expect(component.find('.ui-drawer')).toHaveLength(1);
    });

    it('renders the primary drawer', () => {
      expect(component.find('.ui-drawer--primary')).toHaveLength(1);
    });
  });

  describe('closed drawer with secondary location', () => {
    beforeAll(() => {
      props = {
        id: 'id-1234',
        className: 'className-1234',
        open: false,
        secondary: true,
        width: '250px',
        onEscClick: jest.fn(),
        children: <div>Hello World</div>,
      };

      component = shallow(<Drawer {...props} />);
    });

    it('renders', () => {
      expect(component.find('.ui-drawer')).toHaveLength(1);
    });

    it('renders the secondary drawer', () => {
      expect(component.find('.ui-drawer--secondary')).toHaveLength(1);
    });
  });
});
