import React from 'react';
import { shallow } from 'enzyme';
import Hamburger from 'components/Display/Hamburger';

describe('Hamburger', () => {
  let props;
  let component;
  let onMenuClickSpy;

  beforeAll(() => {
    onMenuClickSpy = jest.fn();
    props = {
      id: 'id-1234',
      className: 'className-1234',
      onMenuClick: onMenuClickSpy,
      opened: false,
    };

    component = shallow(<Hamburger {...props} />);
  });

  it('renders', () => {
    expect(component.find('.ui-hamburger')).toHaveLength(1);
  });

  it('should be closed', () => {
    expect(component.find('.ui-hamburger__bar1--opened')).toHaveLength(0);
  });

  it('calls onMenuClick', () => {
    component.find('.ui-hamburger').simulate('click');
    expect(onMenuClickSpy).toHaveBeenCalled();
  });
});
