import React from 'react';
import { shallow } from 'enzyme';
import MenuItem from 'components/Display/Menu/components/MenuItem';
import renderer from 'react-test-renderer';

let props;
let component;

describe('MenuItem', () => {
  beforeAll(() => {
    props = {
      id: 'id-1234',
      className: 'class-1234',
      primaryNode: 'Primary Text',
      onTouchTap: jest.fn(),
      togglePopover: jest.fn(),
      handleParentMenuOnTouchTap: jest.fn(),
    };
  });

  it('renders', () => {
    const tree = renderer.create(<MenuItem {...props} />);
    expect(tree).toMatchSnapshot();
  });
});

describe('MenuItem with children menuItems', () => {
  beforeAll(() => {
    props = {
      id: 'id-1234',
      className: 'class-1234',
      primaryNode: 'Primary Text',
      onTouchTap: jest.fn(),
      onKeyDown: jest.fn(),
      togglePopover: jest.fn(),
      handleParentMenuOnTouchTap: jest.fn(),
      menuItems: [<MenuItem key={1}/>, <MenuItem key={2}/>],
    };
  });

  it('renders anchorElement if the menuItem contains', () => {
    const tree = renderer.create(<MenuItem {...props} />);
    expect(tree).toMatchSnapshot();
  });
});
