import React from 'react';
import { shallow } from 'enzyme';
import Menu from 'components/Display/Menu';

describe('Menu', () => {
  let props;
  let component;

  beforeAll(() => {
    props = {
      id: 'id-1234',
      className: 'class-1234',
      anchorElement: <div className="anchor-element">My Anchor point</div>,
      position: 'bottom-end',
      children: [],
    };

    component = shallow(<Menu {...props} />);
  });

  it('renders', () => {
    expect(component.find('.ui-menu__list')).toHaveLength(1);
  });

  it('is hidden', () => {
    expect(component.find('.ui-menu__list--hidden')).toHaveLength(1);
  });

  it('renders the anchor element', () => {
    expect(component.find('.anchor-element')).toHaveLength(1);
  });

  it('is positioned at the bottom-end', () => {
    expect(component.find('.ui-menu__list--bottom-end')).toHaveLength(1);
  });

  describe('open and close menu', () => {
    it('opens the menu on anchor click', () => {
      const anchorElement = component.find('.ui-menu__anchor');
      anchorElement.simulate('click');
      expect(component.find('.ui-menu__list--hidden')).toHaveLength(0);
    });
    it('closes the menu on a second anchor click', () => {
      const anchorElement = component.find('.ui-menu__anchor');
      anchorElement.simulate('click');
      expect(component.find('.ui-menu__list--hidden')).toHaveLength(1);
    });
  });
});
