import React from 'react';
import { shallow } from 'enzyme';
import Popover from 'components/Display/Popover';

describe('Popover', () => {
  it('renders the popover component', () => {
    const component = shallow(
      <div>
        <div id="button" className="popover-button">
          This is the reference point
        </div>
        <Popover
          className="popover"
          target="button"
          body="This is the body"
        />
      </div>,
    );
    expect(component.find('.popover')).toHaveLength(1);
  });
});
