import React from 'react';
import { shallow } from 'enzyme';
import SelectionBubble from '../../../src/components/Display/SelectionBubble';

describe('SelectionBubble', () => {
  const selected = {
    yes: 'no',
  };
  it('renders', () => {
    const props = {
      label: '12',
      onTouchTap: jest.fn,
      selected,
    };
    const component = shallow(<SelectionBubble {...props} />);

    expect(component.find('.ui-selection')).toHaveLength(1);
  });

  it('has a ui-selection class', () => {
    const props = {
      label: '999',
      onTouchTap: jest.fn,
      selected,
    };
    const component = shallow(<SelectionBubble {...props} />);

    expect(component.hasClass('ui-selection')).toBeTruthy();
  });
});
