import React from 'react';
import { shallow } from 'enzyme';
import TableRowColumn from 'components/Display/Table/components/TableRowColumn';

describe('TableRowColumn', () => {
  let props;
  let component;
  let onCellClickSpy;

  beforeAll(() => {
    onCellClickSpy = jest.fn();
    props = {
      id: '1234',
      className: 'ui-table-row-column',
      children: '',
      onCellClick: onCellClickSpy,
      columnIndex: 0,
      parent: null,
      secondaryDataList: [],
      style: {},
    };
    component = shallow(<TableRowColumn {...props} />);
  });

  it('renders the table row column', () => {
    expect(component.find('.ui-table-row-column')).toHaveLength(1);
  });

  it('calls the onCellClick prop function', () => {
    component.find('.ui-table-row-column').simulate('click', { stopPropagation() {} });
    expect(onCellClickSpy).toBeCalled();
  });
});
