import React from 'react';
import { shallow } from 'enzyme';
import TableTitle from 'components/Display/Table/components/TableTitle';

describe('TableTitle', () => {
  let props;
  let component;

  beforeAll(() => {
    props = {
      id: '1234',
      className: 'ui-table-row',
      secondary: 'This is a secondary text',
      onSecondaryTitleClick: jest.fn(),
      style: {},
      secondaryStyle: {},
      children: '',
    };
    component = shallow(<TableTitle {...props} />);
  });

  it('renders the table title', () => {
    expect(component.find('.ui-table-title')).toHaveLength(1);
  });

  it('renders the secondary information', () => {
    expect(component.find('.ui-table-title__secondary-title').text()).toBe('This is a secondary text');
  });
});
