import React from 'react';
import { shallow } from 'enzyme';
import Tab from 'components/Display/Tabs/components/Tab';

describe('Tab', () => {
  let props;
  let component;
  let onChangeSpy;

  beforeAll(() => {
    onChangeSpy = jest.fn();

    props = {
      id: 'id-1234',
      className: 'className-1234',
      value: 'myValue',
      onChange: onChangeSpy,
      label: 'My Label',
      activeTab: 'myValue',
      children: <div>Hello World</div>,
    };

    component = shallow(<Tab {...props} />);
  });

  it('renders', () => {
    expect(component.find('.ui-tab')).toHaveLength(1);
  });

  it('should render the label', () => {
    expect(component.find('.ui-tab__label')).toHaveLength(1);
  });

  it('calls the onChange prop function when clicked', () => {
    component.find('.ui-tab').simulate('click');
    expect(onChangeSpy).toBeCalled();
  });
});
