import { decodeHTML, stripHTML } from 'components/Form/utils';

describe('stripHTML', () => {
  it('replaces > and < characters with empty space', () => {
    const HTML = '<svg/onload=alert(1)>';
    const expected = '&lt;svg/onload=alert(1)&gt;';

    expect(stripHTML(HTML)).toEqual(expected);
  });

  it('replaces " with &quote;', () => {
    const HTML = 'this is a tes"t string';
    const expected = 'this is a tes&quot;t string';

    expect(stripHTML(HTML)).toEqual(expected);
  });
});

describe('decodeHTML', () => {
  it('decodes HTML entities to regular HTML characters', () => {
    const doubleQuoteEntity = '&lt;&quot;&gt;';
    const singleQuoteEntity = '&lt;&apos;&gt;';
    const expectedDoubleQuote = '<">';
    const expectedSingleQuote = "<'>";

    expect(decodeHTML(doubleQuoteEntity)).toEqual(expectedDoubleQuote);
    expect(decodeHTML(singleQuoteEntity)).toEqual(expectedSingleQuote);
  });
});
