import React from 'react';
import { shallow } from 'enzyme';
import { FormInputContainer } from '../../src/components/internal';

describe('FormInputContainer', () => {
  it('renders', () => {
    const props = {
      className: 'test-1',
      styles: {},
      children: (<div>Hello</div>),
    };
    const comp = shallow(<FormInputContainer {...props} />);
    expect(comp.find('.test-1')).toHaveLength(1);
    expect(comp.find('.test-1').prop('style')).toBeUndefined();
    expect(comp.text()).toBe('Hello');
  });
  it('passes styles to the divs', () => {
    const props = {
      className: 'test-1',
      styles: {
        container: { height: '10px' },
        hint: { height: '10px' },
        error: { height: '10px' },
      },
      children: (<div>Hello</div>),
    };
    const comp = shallow(<FormInputContainer {...props} />);
    expect(comp.find('.test-1')).toHaveLength(1);
    expect(comp.text()).toBe('Hello');
    expect(comp.find('.test-1').prop('style')).toEqual({
      height: '10px',
    });
  });
});
