import { navigateOnEnterOrSpace } from '../../../src/components/utils/Browser';

describe('navigateOnEnterOrSpace', () => {
  let mockCallback;

  beforeEach(() => {
    mockCallback = jest.fn();
  });

  it('invokes callback when receiving event with enter keycode', () => {
    const event = { keyCode: 13, preventDefault: jest.fn() };
    navigateOnEnterOrSpace(mockCallback)(event);
    expect(mockCallback).toHaveBeenCalledWith(event);
  });

  it('does not invoke callback when receiving invalid event', () => {
    navigateOnEnterOrSpace(mockCallback)({ keyCode: 999 });
    expect(mockCallback).not.toHaveBeenCalled();
  });
});
