import { maskValue } from '../../../src/components/utils/Dates';

const format = 'MM/DD/YYYY';

describe('maskValue', () => {
  it('returns an empty string for empty value', () => {
    expect(maskValue(null, format)).toBe('');
    expect(maskValue(undefined, format)).toBe('');
    expect(maskValue('', format)).toBe('');
  });

  it('returns the value if value is shorter than the first separator index', () => {
    expect(maskValue('12', format)).toBe('12');
  });

  it('adds the separators when needed', () => {
    expect(maskValue('123', format)).toBe('12/3');
    expect(maskValue('12/3', format)).toBe('12/3');
    expect(maskValue('12/34', format)).toBe('12/34');
    expect(maskValue('12342000', format)).toBe('12/34/2000');
    expect(maskValue('12/34/2000', format)).toBe('12/34/2000');
  });
});
