import moment from 'moment';
import { shouldDisableDay } from '../../../src/components/utils/Dates';

describe('shouldDisableDay', () => {
  it('returns false if no limitation', () => {
    const day = moment();
    expect(shouldDisableDay(day)).toBeFalsy();
  });

  it('returns true if day >= maxDate', () => {
    const day = moment();
    expect(shouldDisableDay(day, moment().subtract(2, 'day'))).toBeTruthy();
  });

  it('returns false if day === maxDate', () => {
    const day = moment();
    expect(shouldDisableDay(day, moment())).toBeFalsy();
  });

  it('returns true if day <= minDate', () => {
    const day = moment();
    expect(shouldDisableDay(day, null, moment().add(2, 'day'))).toBeTruthy();
  });

  it('returns true if day >= maxDate', () => {
    const day = moment();
    const check = date => date.isSame(moment(), 'day');
    expect(shouldDisableDay(day, null, null, check)).toBeTruthy();
  });
});
