#!/bin/bash

BLUE='\e[94m'
RED='\e[91m'
NC='\e[39m'
logo=`cat ./uu.txt`

clear

echo -e "${BLUE}${logo}${NC}"
echo ""
echo ""
echo "Where do you want to copy the build?"

select dest in "front-end" "back-office-app" "uniteus-widgets" "uniteus-client" "front-end/packages/uniteus-client-utils" "uniteus-screening" "uniteus-shares" "uniteus-emr";
do
  echo "dest: ${dest}"
  if [ $dest ]
  then
    echo "You picked $dest ($REPLY), building stuff..."
    break
  fi
  echo -e "${RED}Seriously? Just pick an option...${NC}"
done

npm run build:dist

echo "Copying files"
rsync -v -r -R ./dist ../../../${dest}/node_modules/@unite-us/ui

echo -e "${BLUE}All is done !${NC}"
